import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import styled from 'styled-components'

const Wrapper = styled.div`

  min-height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
  align-items: center;

`  

const TicTacToeBox = styled.div`

  background-color: pink;
  height: 150px;
  width: 150px;
  margin: 10px;

`

const TicTacToeCont = styled.div`

  display: flex;
  flex-wrap: wrap;
  max-width: 510px;


`  

function createTicTacToeBoard() {

  for (var i = 0; i < 9; i++) {

  }

}

class TicTacToeBoard extends Component {

  state = {
    game: [0,0,0,0,0,0,0,0,0],
    turn: [1]
  }

  // 0 = blank
  // 1 = X
  // 2 = O

  markBox(index) {
    this.setState()
  }

  render() {
    return(
      <TicTacToeCont>
        <TicTacToeBox index={this.state.game[0]} onClick={this.markBox(this.state.game[0])}/>
        <TicTacToeBox index={this.state.game[1]} />
        <TicTacToeBox index={this.state.game[2]} />
        <TicTacToeBox index={this.state.game[3]} />
        <TicTacToeBox index={this.state.game[4]} />
        <TicTacToeBox index={this.state.game[5]} />
        <TicTacToeBox index={this.state.game[6]} />
        <TicTacToeBox index={this.state.game[7]} />
        <TicTacToeBox index={this.state.game[8]} />
      </TicTacToeCont>
    )
  }

}

function App() {
  return (
    <Wrapper>
      <TicTacToeBoard />
    </Wrapper>
  );
}

export default App;
